initHoverButtons=function(){var t={};t.animDuration=500,$(".styledButton").on("mouseenter",function(i){setWidth($(this),t)}),$(".styledButton").on("mouseleave",function(i){resetWidth($(this),t)})},annimateAddNumbers=function(t,i,n,e){i>e&&setTimeout(function(){t.html(e+2+"px"),annimateAddNumbers(t,i,n,e+2),console.log(i)},n/i)},annimateRemoveNumbers=function(t,i,n,e){e>-1&&setTimeout(function(){t.html(e+"px"),annimateRemoveNumbers(t,i,n,e-1)},n/i)},setWidth=function(t,i){t.find(".rule").stop().fadeIn(200),t.find(".rule.top > div").css({width:"100%"}),t.find(".rule.right > div").css({height:"100%"}),annimateAddNumbers(t.find(".rule.top > p"),t.width(),i.animDuration,0),annimateAddNumbers(t.find(".rule.right > p"),t.height(),i.animDuration,0)},resetWidth=function(t,i){t.find(".rule.top > div").css({width:"0px"}),t.find(".rule.right > div").css({height:"0px"}),annimateRemoveNumbers(t.find(".rule.top > p"),t.width(),i.animDuration,t.width()),annimateRemoveNumbers(t.find(".rule.right > p"),t.height(),i.animDuration,t.height()),t.find(".rule").stop().delay(300).fadeOut(200)};